const React = require('react');

function Layout({ title, children }) {
  return (
    <html lang="en" className="h-100">
      <head>
        <meta charSet="UTF-8" />
        <meta httpEquiv="X-UA-Compatible" content="IE=edge" />
        <meta name="viewport" content="width=device-width, initial-scale=1.0" />
